# Installation form $formInstall = New-Object System.Windows.Forms.Form $formInstall.Text = "Installing Applications" $formInstall.Size = New-Object System.Drawing.Size(400,300) $formInstall.StartPosition = "Manual" $formInstall.Location = New-Object System.Drawing.Point(80, 80)
$labelInfo = New-Object System.Windows.Forms.Label $labelInfo.Size = New-Object System.Drawing.Size(360, 40) $labelInfo.Location = New-Object System.Drawing.Point(10, 10) $labelInfo.Text = "This may take a while. Please leave the computer on and do not close this window until the installation is complete." $labelInfo.AutoSize = $false $labelInfo.TextAlign = 'TopLeft' $labelInfo.Font = New-Object System.Drawing.Font("Segoe UI", 9, [System.Drawing.FontStyle]::Regular) $formInstall.Controls.Add($labelInfo)
$listBox = New-Object System.Windows.Forms.ListBox $listBox.Size = New-Object System.Drawing.Size(360,190) $listBox.Location = New-Object System.Drawing.Point(10, 60) $formInstall.Controls.Add($listBox)
foreach ($app in $applications) { $listBox.Items.Add("‚è≥ Waiting: $($app.Name)") }
$formInstall.Show()
$failedApps = @()
# First pass for ($i = 0; $i -lt $applications.Count; $i++) { $app = $applications[$i] $listBox.Items[$i] = "üîß Installing: $($app.Name)" $formInstall.Refresh() try { irm $app.Url | iex $listBox.Items[$i] = "‚úÖ Installed: $($app.Name)" } catch { $listBox.Items[$i] = "‚ùå Failed: $($app.Name)" $errorMessage = "$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss') - Failed to install $($app.Name): $($_.Exception.Message)" Add-Content -Path $logPath -Value $errorMessage $failedApps += $app } Start-Sleep -Seconds 1 }
# Retry pass if ($failedApps.Count -gt 0) { Add-Content -Path $logPath -Value "`n--- RETRYING FAILED INSTALLATIONS ---`n" foreach ($app in $failedApps) { $index = $applications.IndexOf($app) $listBox.Items[$index] = "üîÅ Retrying: $($app.Name)" $formInstall.Refresh() try { irm $app.Url | iex $listBox.Items[$index] = "‚úÖ Installed on Retry: $($app.Name)" } catch { $listBox.Items[$index] = "‚ùå Still Failed: $($app.Name)" $errorMessage = "$(Get-Date -Format 'yyyy-MM-dd HH:mm:ss') - Retry failed for $($app.Name): $($_.Exception.Message)" Add-Content -Path $logPath -Value $errorMessage } Start-Sleep -Seconds 1 } }
Start-Sleep -Seconds 300 $formInstall.Close()

